import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { gql, useLazyQuery, useSubscription } from "@apollo/client";
import { Client } from "@bandprotocol/bandchain.js";
import { getPairPrice, sendCoin, undelegateCoin } from "../band";

export default function DelegatorDetails() {
  const [isShow, setisShow] = useState(false);
  const [isShowUndelegateModal, setisShowUndelegateModal] = useState(false);
  const [delegateTxHash, setdelegateTxHash] = useState("");
  const [undelegateTxHash, setundelegateTxHash] = useState("");
  const [loadingDelegate, setloadingDelegate] = useState(false);
  const [loadingUndelegate, setloadingUndelegate] = useState(false);
  const [delegateAmount, setDelegateAmount] = useState(0);
  const [undelegateAmount, setUndelegateAmount] = useState(0);
  const closeModalHandler = () => {
    setloadingDelegate(false);
    setloadingUndelegate(false);
    setisShow(false);
    setisShowUndelegateModal(false);
  };
  const wallet = useSelector((state) => state.wallet);
  let { operator } = useParams();

  // states
  const [usdPrice, setUsdPrice] = useState([]);

  async function getPrice() {
    const response = await getPairPrice(["BAND/USD"], 10, 16);
    setUsdPrice(response[0].rate);
  }

  const GET_DELEGATIONS = gql`
    subscription GetAccountDelegations($address: String!) {
      delegations_view(
        where: { delegator_address: { _eq: $address } }
        order_by: { moniker: asc }
      ) {
        amount
        moniker
        reward
        operator_address
        delegator_address
      }
    }
  `;

  const GET_VALIDATOR_DETAILS = gql`
    query GetValidatorDetails($address: String!, $operator: String!) {
      delegations_view(
        where: { delegator_address: { _eq: $address }, operator_address: { _eq: $operator } }
      ) {
        amount
        delegator_address
        moniker
        operator_address
        reward
      }
      validators(where: { operator_address: { _eq: $operator } }) {
        tokens
        commission_rate
        validator_report_count {
          count
        }
      }
      validators_aggregate {
        aggregate {
          sum {
            tokens
          }
        }
      }
    }
  `;

  // const { loading, error, data } = useQuery(GET_DELEGATIONS);
  // const [GetValidator, { loading, error, data }] = useSubscription(GET_VALIDATOR_DETAILS);
  //
  const [GetValidator, { loading, error, data }] = useLazyQuery(GET_VALIDATOR_DETAILS);

  // const [getDelegationSub, { loading, error, data }] = useSubscription(GET_DELEGATIONS, {
  //   variables: { address: wallet.address },
  // });

  // const { amount, moniker, delegator_address, operator_address, reward, share_percentage } = data.delegations_view

  const confirmDelegate = async () => {
    setloadingDelegate(true);
    const response = await sendCoin(
      operator,
      delegateAmount,
      wallet.privateKey,
      wallet.pubkey,
      wallet.address,
      "delegate"
    );
    if (response) {
      setdelegateTxHash(response.txhash);
    }
  };

  const confirmUndelegate = async () => {
    setloadingUndelegate(true);
    const response = await undelegateCoin(
      operator,
      undelegateAmount,
      wallet.privateKey,
      wallet.pubkey,
      wallet.address
    );
    if (response) {
      console.log(response);
      setundelegateTxHash(response.txhash);
    }
  };

  const openModalDelegate = () => {
    setisShow(true);
  };

  const openModalUndelegate = () => {
    setisShowUndelegateModal(true);
  };

  useEffect(() => {
    getPrice();
  }, []);

  useEffect(() => {
    GetValidator({ variables: { address: wallet.address, operator: operator } });
  }, [wallet, operator, GetValidator]);

  return (
    <div>
      <div className="flex flex-wrap -mx-2">
        {loading ? (
          "Loading..."
        ) : error ? (
          `Error! ${error.message}`
        ) : data ? (
          <>
            <h1 className="text-2xl mb-3">
              <strong>{data.delegations_view[0].moniker}</strong>
            </h1>
            <div className="flex w-full gap-3 flex-wrap mt-3">
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow bg-rose-400">
                <h3 className="mb-2 text-sm font-medium">Voting Power</h3>
                <h3 className="text-2xl text-right font-bold">
                  {(
                    (data.validators[0].tokens / data.validators_aggregate.aggregate.sum.tokens) *
                    100
                  ).toFixed(2)}
                  %
                </h3>
              </div>
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow bg-indigo-400">
                <h3 className="mb-2 text-sm font-medium">Commission</h3>
                <h3 className="text-2xl text-right font-bold">
                  {(data.validators[0].commission_rate * 100).toFixed(2)}%
                </h3>
              </div>
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow bg-warmGray-400">
                <h3 className="mb-2 text-sm font-medium">Oracle Reports</h3>
                <h3 className="text-2xl text-right font-bold">
                  {data.validators[0].validator_report_count.count}
                </h3>
              </div>
            </div>
            <div className="mt-8 block w-full">
              <h2 className="text-xl mb-3">
                <strong>Your Delegation</strong>
              </h2>
              <div className="flex w-full gap-3 flex-wrap mt-3">
                <div className="border-2 border-black p-5 w-full block rounded-xl flex-1 flex-grow bg-yellow-300">
                  <h3 className="mb-4 text-xl font-bold">Assets</h3>
                  <h3 className="mb-2 text-sm font-medium">BAND Staked</h3>
                  <h4 className="text-2xl font-bold text-right">
                    {(data.delegations_view[0].amount / 1e6).toFixed(4)}
                  </h4>
                  <h4 className="mb-2 text-sm font-medium text-right text-orange">
                    ~{((data.delegations_view[0].amount / 1e6) * usdPrice).toFixed(4)} USD
                  </h4>
                  <div className="action-group flex gap-3 mt-4">
                    <button
                      onClick={openModalDelegate}
                      className="flex-1 flex-grow button block ml-auto text-sm text-black hover:text-white bg-white hover:bg-black border-2 border-black focus:outline-none focus:ring-black focus:ring-opacity-50  py-2 px-10 rounded-xl focus:outline-none disabled:opacity-50 shadow-insetBlack hover:shadow-none"
                    >
                      Delegate
                    </button>
                    <button
                      onClick={openModalUndelegate}
                      className="flex-1 flex-grow button block ml-auto text-sm text-black hover:text-white bg-white hover:bg-black border-2 border-black focus:outline-none focus:ring-black focus:ring-opacity-50  py-2 px-10 rounded-xl focus:outline-none disabled:opacity-50 shadow-insetBlack hover:shadow-none"
                    >
                      Undelegate
                    </button>
                    <button className="flex-1 flex-grow button block ml-auto text-sm text-black hover:text-white bg-white hover:bg-black border-2 border-black focus:outline-none focus:ring-black focus:ring-opacity-50  py-2 px-10 rounded-xl focus:outline-none disabled:opacity-50 shadow-insetBlack hover:shadow-none">
                      Redelegate
                    </button>
                  </div>
                </div>
                <div className="border-2 border-black p-5 w-full block rounded-xl flex-1 flex-grow bg-teal-400">
                  <h3 className="mb-3 text-xl font-bold">Rewards</h3>
                  <h3 className="mb-2 text-sm font-medium">Unclaim Rewards (BAND)</h3>
                  <h4 className="text-2xl font-bold text-right">
                    {(data.delegations_view[0].reward / 1e6).toFixed(4)}
                  </h4>
                  <h4 className="mb-2 text-sm font-medium text-right text-orange">
                    ~{((data.delegations_view[0].reward / 1e6) * usdPrice).toFixed(4)} USD
                  </h4>
                  <div className="action-group flex gap-3 mt-4">
                    <button className="flex-1 flex-grow button block ml-auto text-sm text-black hover:text-white bg-white hover:bg-black border-2 border-black focus:outline-none focus:ring-black focus:ring-opacity-50  py-2 px-10 rounded-xl focus:outline-none disabled:opacity-50 shadow-insetBlack hover:shadow-none">
                      Reinvest
                    </button>
                    <button className="flex-1 flex-grow button block ml-auto text-sm text-black hover:text-white bg-white hover:bg-black border-2 border-black focus:outline-none focus:ring-black focus:ring-opacity-50  py-2 px-10 rounded-xl focus:outline-none disabled:opacity-50 shadow-insetBlack hover:shadow-none">
                      Claim
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </>
        ) : (
          "0 Validator"
        )}
      </div>

      <div
        aria-labelledby="modal-title"
        role="dialog"
        aria-modal="true"
        className={
          isShow
            ? "fixed z-10 inset-0 overflow-y-auto block"
            : " fixed z-10 inset-0 overflow-y-auto hidden"
        }
      >
        <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
          <div
            className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"
            aria-hidden="true"
          ></div>

          <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">
            &#8203;
          </span>

          <div className="inline-block align-bottom bg-white rounded-3xl text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-5">
              <div className="flex justify-between align-items-center mt-3 mb-5 text-center sm:mt-0 sm:text-left">
                <h2 className="mb-0 text-xl">
                  <strong>Delegate</strong>
                </h2>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6 text-orange cursor-pointer hover:text-orange-dark"
                  onClick={(e) => closeModalHandler(e)}
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </div>
              {loadingDelegate ? (
                delegateTxHash ? (
                  <div>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      class="h-20 w-20 text-green-400 mx-auto mb-5"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth="2"
                        d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                      />
                    </svg>
                    <h4 className="text-md text-center">
                      <strong>Transaction Hash</strong>
                    </h4>
                    <a
                      className="overflow-ellipsis overflow-hidden text-black mb-3 block hover:text-blue"
                      href={`https://laozi-testnet4.cosmoscan.io/tx/${delegateTxHash}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      {delegateTxHash}
                    </a>
                  </div>
                ) : (
                  <div>
                    <h2>Sending...</h2>
                  </div>
                )
              ) : (
                <div className="modal-body">
                  <div className="form-delegate-wrapper mb-5">
                    <div className="input-group mb-3">
                      <label
                        htmlFor="input-address"
                        className="block text-sm font-medium text-gray-700 mb-1"
                      >
                        Delegate To
                      </label>
                      <p>{operator}</p>
                    </div>
                    <div className="input-group mb-3">
                      <label
                        htmlFor="input-amount"
                        className="block text-sm font-medium text-gray-700 mb-1"
                      >
                        Amount
                      </label>
                      <input
                        className="focus:outline-none focus:ring-2 focus:ring-gray-200 block w-full p-2 sm:text-sm border-solid border-2 border-gray-200 rounded-md"
                        type="number"
                        id="input-amount"
                        valur={delegateAmount}
                        onChange={(e) => setDelegateAmount(e.target.value)}
                      />
                    </div>
                  </div>
                  <button
                    onClick={(e) => confirmDelegate(e)}
                    className="button block w-full bg-purple-600 text-white py-2 px-4 rounded-xl hover:bg-purple-700 focus:outline-none focus:ring-purple-600 focus:ring-opacity-50 transition duration-500 ease-in-out"
                  >
                    Delegate
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <div
        aria-labelledby="modal-title"
        role="dialog"
        aria-modal="true"
        className={
          isShowUndelegateModal
            ? "fixed z-10 inset-0 overflow-y-auto block"
            : " fixed z-10 inset-0 overflow-y-auto hidden"
        }
      >
        <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
          <div
            className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"
            aria-hidden="true"
          ></div>

          <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">
            &#8203;
          </span>

          <div className="inline-block align-bottom bg-white rounded-3xl text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-5">
              <div className="flex justify-between align-items-center mt-3 mb-5 text-center sm:mt-0 sm:text-left">
                <h2 className="mb-0 text-xl">
                  <strong>Undelegate</strong>
                </h2>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6 text-orange cursor-pointer hover:text-orange-dark"
                  onClick={(e) => closeModalHandler(e)}
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </div>
              {loadingUndelegate ? (
                undelegateTxHash ? (
                  <div>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      class="h-20 w-20 text-green-400 mx-auto mb-5"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth="2"
                        d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
                      />
                    </svg>
                    <h4 className="text-md text-center">
                      <strong>Transaction Hash</strong>
                    </h4>
                    <a
                      className="overflow-ellipsis overflow-hidden text-black mb-3 block hover:text-blue"
                      href={`https://laozi-testnet4.cosmoscan.io/tx/${undelegateTxHash}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      {undelegateTxHash}
                    </a>
                  </div>
                ) : (
                  <div>
                    <h2>Sending...</h2>
                  </div>
                )
              ) : (
                <div className="modal-body">
                  <div className="form-delegate-wrapper mb-5">
                    <div className="input-group mb-3">
                      <label
                        htmlFor="input-address"
                        className="block text-sm font-medium text-gray-700 mb-1"
                      >
                        Undelegate From
                      </label>
                      <p>{operator}</p>
                    </div>
                    <div className="input-group mb-3">
                      <label
                        htmlFor="input-amount"
                        className="block text-sm font-medium text-gray-700 mb-1"
                      >
                        Amount
                      </label>
                      <input
                        className="focus:outline-none focus:ring-2 focus:ring-gray-200 block w-full p-2 sm:text-sm border-solid border-2 border-gray-200 rounded-md"
                        type="number"
                        id="input-amount"
                        valur={undelegateAmount}
                        onChange={(e) => setUndelegateAmount(e.target.value)}
                      />
                    </div>
                  </div>
                  <button
                    onClick={(e) => confirmUndelegate(e)}
                    className="button block w-full bg-purple-600 text-white py-2 px-4 rounded-xl hover:bg-purple-700 focus:outline-none focus:ring-purple-600 focus:ring-opacity-50 transition duration-500 ease-in-out"
                  >
                    Undelegate
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
