import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { gql, useLazyQuery, useSubscription } from "@apollo/client";

export default function DelegatorDetails() {
  const wallet = useSelector((state) => state.wallet);
  let { operator } = useParams();
  const GET_VALIDATOR_DETAILS = gql`
    subscription GetValidatorDetails($address: String!, $operator: String!) {
      delegations_view(
        where: { delegator_address: { _eq: $address }, operator_address: { _eq: $operator } }
      ) {
        amount
        delegator_address
        moniker
        operator_address
        reward
        share_percentage
      }
    }
  `;

  // const [GetValidator, { loading, error, data }] = useSubscription(GET_VALIDATOR_DETAILS);
  const { loading, error, data } = useSubscription(GET_VALIDATOR_DETAILS, {
    variables: { address: wallet.address, operator: operator },
  });

  // const { amount, moniker, delegator_address, operator_address, reward, share_percentage } = data.delegations_view

  useEffect(() => {
    // GetValidator({ variables: { address: wallet.address, operator: operator } });
  }, []);

  return (
    <div>
      <div className="flex flex-wrap -mx-2">
        {loading
          ? "Loading..."
          : error
          ? `Error! ${error.message}`
          : data
          ? data.delegations_view.map((delegator, ind) => {
              return (
                <>
                  <h1 className="text-2xl">
                    <strong>{delegator.moniker}</strong>
                  </h1>
                  <div className="flex"></div>
                </>
              );
            })
          : "0 Validator"}
      </div>
    </div>
  );
}
