import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { gql, useLazyQuery } from "@apollo/client";

export default function DelegatorDetails() {
  const wallet = useSelector((state) => state.wallet);
  let { operator } = useParams();
  const GET_VALIDATOR_DETAILS = gql`
    query GetValidatorDetails($address: String!) {
      delegations_view(
        where: {
          delegator_address: { _eq: wallet.address }
          operator_address: { _eq: operator }
        }
      ) {
        amount
        delegator_address
        identity
        moniker
        operator_address
        reward
      }
    }
  `;

  const [GetValidatorDetails, { loading, error, data }] = useLazyQuery(GET_VALIDATOR_DETAILS);

  useEffect(() => {
    GetValidatorDetails({ variables: { address: operator } });
  }, []);

  return (
    <div>
      <h1>Operator Address : {operator}</h1>
    </div>
  );
}
