import AccountSidebar from "../components/AccountSidebar";
import { gql, useQuery } from "@apollo/client";
import { useState } from "react";

export default function AccountPage() {
  const GET_VALIDATORS = gql`
    query GetValidators {
      validators {
        account_id
        moniker
        account {
          address
          balance
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_VALIDATORS);

  return (
    <div className="container mx-auto max-w-full">
      <div className="md:flex gap-6">
        <AccountSidebar />
        <div className="card bg-white md:p-10 p-5 rounded-2xl mt-5 lg:w-3/4 w-full">
          {loading
            ? "Loading..."
            : error
            ? `Error! ${error.message}`
            : data
            ? data.validators.map((validator, ind) => {
                return <div key={ind}>{validator.moniker}</div>;
              })
            : "0 Validator"}
        </div>
      </div>
    </div>
  );
}
