import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { gql, useQuery } from "@apollo/client";
import { SingleValidator } from "../components/SingleValidator";

export default function Delegations() {
  const wallet = useSelector((state) => state.wallet);

  const GET_DELEGATIONS = gql`
    query GetAccountDelegations {
      delegations_view(
        where: { delegator_address: { _eq: "band1jrhuqrymzt4mnvgw8cvy3s9zhx3jj0dq30qpte" } }
      ) {
        amount
        moniker
        reward
        operator_address
        delegator_address
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_DELEGATIONS);
  return (
    <div className="flex flex-wrap -mx-2">
      Delegations
      {/* {loading
        ? "Loading..."
        : error
        ? `Error! ${error.message}`
        : data
        ? data.validators.map((validator, ind) => {
            return <SingleValidator validator={validator} />;
          })
        : "0 Validator"} */}
    </div>
  );
}
