import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { gql, useLazyQuery, useSubscription } from "@apollo/client";

export default function DelegatorDetails() {
  const [info, setInfo] = useState(null);
  const [validatorsTokens, setValidatorsTokens] = useState(null);
  const [bondedTokens, setBondedTokens] = useState(null);
  const wallet = useSelector((state) => state.wallet);
  let { operator } = useParams();
  // const VALIDATOR_QUERY = gql`
  //   query GetValidatorInfo($address: String!, $operator: String!) {
  //     delegations_view(
  //       where: { delegator_address: { _eq: $address }, operator_address: { _eq: $operator } }
  //     ) {
  //       operator_address
  //       moniker
  //     }
  //     validators(where: { operator_address: { _eq: $operator } }) {
  //       tokens
  //     }
  //     validators_aggregate {
  //       aggregate {
  //         sum {
  //           tokens
  //         }
  //       }
  //     }
  //   }
  // `;

  const GET_VALIDATOR_DETAILS = gql`
    query GetValidatorDetails($address: String!, $operator: String!) {
      delegations_view(
        where: { delegator_address: { _eq: $address }, operator_address: { _eq: $operator } }
      ) {
        amount
        delegator_address
        moniker
        operator_address
        reward
      }
      validators(where: { operator_address: { _eq: $operator } }) {
        tokens
      }
      validators_aggregate {
        aggregate {
          sum {
            tokens
          }
        }
      }
    }
  `;

  // const { loading, error, data } = useQuery(GET_DELEGATIONS);
  // const [GetValidator, { loading, error, data }] = useSubscription(GET_VALIDATOR_DETAILS);
  //
  const [GetValidator, { loading, error, data }] = useLazyQuery(GET_VALIDATOR_DETAILS);

  console.log(data);

  // const { amount, moniker, delegator_address, operator_address, reward, share_percentage } = data.delegations_view

  useEffect(() => {
    GetValidator({ variables: { address: wallet.address, operator: operator } });
  }, [wallet, GetValidator, operator]);

  return (
    <div>
      <div className="flex flex-wrap -mx-2">
        {loading ? (
          "Loading..."
        ) : error ? (
          `Error! ${error.message}`
        ) : data ? (
          <>
            <h1 className="text-2xl">
              {/* <strong>{info.delegations_view[0].moniker}</strong> */}
            </h1>
            <div className="flex w-full gap-3 flex-wrap mt-3">
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                <h3 className="text-gray-600 mb-2 text-sm">Voting Power</h3>
                <h3 className="text-2xl text-right font-bold">
                  {(
                    (data.validators[0].tokens / data.validators_aggregate.aggregate.sum.tokens) *
                    100
                  ).toFixed(2)}
                  %
                </h3>
              </div>
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                <h3 className="text-gray-600 mb-2 text-sm">Commission</h3>
                <h3 className="text-2xl text-right font-bold"></h3>
              </div>
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                <h3 className="text-gray-600 mb-2 text-sm">Uptime</h3>
                <h3 className="text-2xl text-right font-bold"></h3>
              </div>
              <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                <h3 className="text-gray-600 mb-2 text-sm">Oracle Reports</h3>
                <h3 className="text-2xl text-right font-bold"></h3>
              </div>
            </div>
          </>
        ) : (
          "0 Validator"
        )}
      </div>
    </div>
  );
}
