import { useParams } from "react-router-dom";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { gql, useLazyQuery, useSubscription } from "@apollo/client";

export default function DelegatorDetails() {
  const wallet = useSelector((state) => state.wallet);
  let { operator } = useParams();
  const VALIDATOR_QUERY = gql`
  qury GetValidatorInfo {
  delegations_view(where: {delegator_address: {_eq: "band1jrhuqrymzt4mnvgw8cvy3s9zhx3jj0dq30qpte"}, operator_address: {_eq: "bandvaloper1zkf9qzs7ayf3uqksxqwve8q693dsdhxk800wvw"}}) {
    operator_address
    moniker
  }
  validators(where: {operator_address: {_eq: "bandvaloper1zkf9qzs7ayf3uqksxqwve8q693dsdhxk800wvw"}}) {
    tokens
  }
  validators_aggregate {
    aggregate {
      sum {
        tokens
      }
    }
  }
  }
  `;

  const GET_VALIDATOR_DETAILS = gql`
    subscription GetValidatorDetails($address: String!, $operator: String!) {
      delegations_view(
        where: { delegator_address: { _eq: $address }, operator_address: { _eq: $operator } }
      ) {
        amount
        delegator_address
        moniker
        operator_address
        reward
      }
    }
  `;

  // const [GetValidator, { loading, error, data }] = useSubscription(GET_VALIDATOR_DETAILS);
  const { loading, error, data } = useSubscription(GET_VALIDATOR_DETAILS, {
    variables: { address: wallet.address, operator: operator },
  });

  console.log(data);

  // const { amount, moniker, delegator_address, operator_address, reward, share_percentage } = data.delegations_view

  useEffect(() => {
    // GetValidator({ variables: { address: wallet.address, operator: operator } });
  }, []);

  return (
    <div>
      <div className="flex flex-wrap -mx-2">
        {loading
          ? "Loading..."
          : error
          ? `Error! ${error.message}`
          : data
          ? data.delegations_view.map((delegator, ind) => {
              return (
                <>
                  <h1 className="text-2xl">
                    <strong>{delegator.moniker}</strong>
                  </h1>
                  <div className="flex w-full gap-3 flex-wrap mt-3">
                    <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                      <h3 className="text-gray-500">Voting Power</h3>
                      <h3 className="text-2xl text-right font-bold">{delegator.amount}</h3>
                    </div>
                    <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                      <h3 className="text-gray-500">Commission</h3>
                      <h3 className="text-2xl text-right font-bold">
                        {delegator.share_percentage}
                      </h3>
                    </div>
                    <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                      <h3 className="text-gray-500">Uptime</h3>
                      <h3 className="text-2xl text-right font-bold">
                        {delegator.share_percentage}
                      </h3>
                    </div>
                    <div className="border-2 border-black rounded-xl p-5 flex-1 flex-grow">
                      <h3 className="text-gray-500">Oracle Reports</h3>
                      <h3 className="text-2xl text-right font-bold">
                        {delegator.share_percentage}
                      </h3>
                    </div>
                  </div>
                </>
              );
            })
          : "0 Validator"}
      </div>
    </div>
  );
}
